mapper.select:
SELECT create_time_min, limit_log_url, limit_log_address_geo, limit_log_address, limit_log_server_id, count(*) count, (select count(*) from monitor_proxy_limit_log where create_time_min = mpll.create_time_min and limit_log_server_id = mpll.limit_log_server_id and limit_log_address = mpll.limit_log_address and limit_log_type = 'allow' and limit_log_url = mpll.limit_log_url) allowCount, (select count(*) from monitor_proxy_limit_log where create_time_min = mpll.create_time_min and limit_log_server_id = mpll.limit_log_server_id and limit_log_address = mpll.limit_log_address and limit_log_type = 'deny' and limit_log_url = mpll.limit_log_url) denyCount, (select count(*) from monitor_proxy_limit_log where create_time_min = mpll.create_time_min and limit_log_server_id = mpll.limit_log_server_id and limit_log_address = mpll.limit_log_address and limit_log_type = 'warn' and limit_log_url = mpll.limit_log_url) warnCount, proxy_name proxyName FROM monitor_proxy_limit_log mpll INNER JOIN monitor_proxy mp ON mp.proxy_id = mpll.limit_log_server_id
AND mpll.limit_log_server_id = #{query.limitLogServerId}
AND mpll.create_time >= #{query.startDate}
AND mpll.create_time <= #{query.endDate}
GROUP BY create_time_min, limit_log_url, limit_log_address_geo, limit_log_address, limit_log_server_id ORDER BY create_time_min DESC

select create_time,
        sum(allowCount) allowCount,
        sum(denyCount) denyCount,
        sum(warnCount) warnCount from(
            SELECT DISTINCT create_time,
                    (select count(*) from monitor_proxy_limit_log where create_time_min = mpll.create_time_min and limit_log_server_id = mpll.limit_log_server_id and  limit_log_address = mpll.limit_log_address and limit_log_type = 'allow' and limit_log_url = mpll.limit_log_url) allowCount,
                    (select count(*) from monitor_proxy_limit_log where create_time_min = mpll.create_time_min and limit_log_server_id = mpll.limit_log_server_id and  limit_log_address = mpll.limit_log_address and limit_log_type = 'deny' and limit_log_url = mpll.limit_log_url) denyCount,
                    (select count(*) from monitor_proxy_limit_log where create_time_min = mpll.create_time_min and limit_log_server_id = mpll.limit_log_server_id and  limit_log_address = mpll.limit_log_address and limit_log_type = 'warn' and limit_log_url = mpll.limit_log_url) warnCount
            FROM monitor_proxy_limit_log mpll


                    AND limit_log_server_id = #{query.limitLogServerId}



                    AND limit_log_address = #{query.limitLogAddress}



                    AND create_time >= #{query.startDate}



                    AND mpll.create_time <= #{query.endDate}


) t
GROUP BY create_time
ORDER BY create_time
