mapper.select:
SELECT monitor_proxy_log_date, monitor_proxy_log_url, monitor_proxy_log_address_geo, monitor_proxy_log_address, monitor_proxy_log_server_id, count(*) count, (select count(*) from monitor_proxy_log where monitor_proxy_log_date = mpll.monitor_proxy_log_date and monitor_proxy_log_server_id = mpll.monitor_proxy_log_server_id and monitor_proxy_log_address = mpll.monitor_proxy_log_address and monitor_proxy_log_type = 'allow' and monitor_proxy_log_url = mpll.monitor_proxy_log_url) allowCount, (select count(*) from monitor_proxy_log where monitor_proxy_log_date = mpll.monitor_proxy_log_date and monitor_proxy_log_server_id = mpll.monitor_proxy_log_server_id and monitor_proxy_log_address = mpll.monitor_proxy_log_address and monitor_proxy_log_type = 'deny' and monitor_proxy_log_url = mpll.monitor_proxy_log_url) denyCount, (select count(*) from monitor_proxy_log where monitor_proxy_log_date = mpll.monitor_proxy_log_date and monitor_proxy_log_server_id = mpll.monitor_proxy_log_server_id and monitor_proxy_log_address = mpll.monitor_proxy_log_address and monitor_proxy_log_type = 'warn' and monitor_proxy_log_url = mpll.monitor_proxy_log_url) warnCount, proxy_name proxyName FROM monitor_proxy_log mpll INNER JOIN monitor_proxy mp ON mp.proxy_id = mpll.monitor_proxy_log_server_id
AND mpll.monitor_proxy_log_server_id = #{query.monitorProxyLogServerId}
AND mpll.create_time >= #{query.startDate}
AND mpll.create_time <= #{query.endDate}
GROUP BY monitor_proxy_log_date, monitor_proxy_log_url, monitor_proxy_log_address_geo, monitor_proxy_log_address, monitor_proxy_log_server_id ORDER BY monitor_proxy_log_date DESC

select create_time,
sum(allowCount) allowCount,
sum(denyCount) denyCount,
sum(warnCount) warnCount from(
SELECT DISTINCT create_time,
(select count(*) from monitor_proxy_log where monitor_proxy_log_date = mpll.monitor_proxy_log_date and monitor_proxy_log_server_id = mpll.monitor_proxy_log_server_id and  monitor_proxy_log_address = mpll.monitor_proxy_log_address and monitor_proxy_log_type = 'allow' and monitor_proxy_log_url = mpll.monitor_proxy_log_url) allowCount,
(select count(*) from monitor_proxy_log where monitor_proxy_log_date = mpll.monitor_proxy_log_date and monitor_proxy_log_server_id = mpll.monitor_proxy_log_server_id and  monitor_proxy_log_address = mpll.monitor_proxy_log_address and monitor_proxy_log_type = 'deny' and monitor_proxy_log_url = mpll.monitor_proxy_log_url) denyCount,
(select count(*) from monitor_proxy_log where monitor_proxy_log_date = mpll.monitor_proxy_log_date and monitor_proxy_log_server_id = mpll.monitor_proxy_log_server_id and  monitor_proxy_log_address = mpll.monitor_proxy_log_address and monitor_proxy_log_type = 'warn' and monitor_proxy_log_url = mpll.monitor_proxy_log_url) warnCount,
FROM monitor_proxy_log mpll


    AND monitor_proxy_log_server_id = #{query.monitorProxyLogServerId}



    AND monitor_proxy_log_address = #{query.monitorProxyLogAddress}



    AND create_time >= #{query.startDate}



    AND mpll.create_time <= #{query.endDate}


) t
GROUP BY create_time
ORDER BY create_time
